import axios from "axios";
require('dotenv').config();

const URL = process.env.URL ;
const API_KEY= process.env.API_KEY;
const fetchWeather = async (query) =>{
    const {data} = await axios.get(URL, {
        params: {
            q: query,
            units: 'metric',
            APPID: API_KEY,
        }
    });
    return data;
}
